<script>
    var acc = document.getElementsByClassName("accordion");
    var i=0;

    for (i = 0; i < acc.length; i++) {
        acc[i].addEventListener("click", function() {
            /* Toggle between adding and removing the "active" class,
            to highlight the button that controls the panel */
            this.classList.toggle("active");

            /* Toggle between hiding and showing the active panel */
            //alert(this.getAttribute('data-id'));
            var panel = document.getElementsByClassName("panel"+this.getAttribute('data-id'));
            for (j = 0; j < panel.length; j++) {
                if (panel[j].style.display === "block") {
                    panel[j].style.display = "none";
                } else {
                    panel[j].style.display = "block";
                }
            }
            //var panel = this.nextElementSibling;
            
        });
    }
</script><?php /**PATH C:\xampp2\htdocs\legacy\resources\views/admin/layout/sidebar.blade.php ENDPATH**/ ?>